def gerar_game(n):

    lista = []
    lista2 = []

    for i in range(n):
        ponto = gerar_pontuacao_2()
        lista.append(ponto)
        cnt = Counter()
        for number in lista:
            cnt[number] += 1
        if cnt[number] < 4:
            if cnt[1] == 3 and cnt[2] == 3:
                pass

                for i in range(100):

                    ponto2 = gerar_pontuacao_2()
                    lista2.append(ponto2)
                    ponto2 = gerar_pontuacao_2()
                    if ponto2 == lista2[0]:
                        pass
                    else:
                        continue


                    break
                break
            pass
        else:
            break






    return cnt, lista2






def game(n, N):

    games_jogador = []




    for i in range(N):

        game, lista3 = gerar_game(n)

        if game[1] == 4:
            games_jogador.append(1)

        elif game[2] == 4:
            games_jogador.append(2)

        elif lista3[0] == 1:
            games_jogador.append(1)


        elif lista3[0] == 2:
            games_jogador.append(2)

        cnt = Counter()

        for number in games_jogador:
            cnt[number] += 1

            if cnt[1] == 6 and cnt[2] == 6:
                continue
            elif cnt[1] == 6 and cnt[2] < 5:
                break
            elif cnt[1] < 5 and cnt[2] == 6:
                break
            elif cnt[1] == 7 and cnt[2] == 5:
                break
            elif cnt[1] == 5 and cnt[2] == 7:
                break
            else:
                continue

            break

    return cnt

